import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Weather } from './Weather.js';
import './style.css'
const PLACES = [
  { name: "Кемерово" },
  { name: "Новосибирск" },
  { name: "Москва" },
  { name: "Топки" },
  { name: "Томск" }
];
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      activePlace: 0,
    };
  }
  render() {
    const activePlace = this.state.activePlace;
    return (
      <div className="container">
        <div className="row block1">
          <select>
          {PLACES.map((place, index) => (
            <option
              className="col-sm-8 mt-2 btn"
              key={index}

            >
              {place.name}
              </option>
          ))}
           </select>
           <button  onClick={() => {
                this.setState({ activePlace: index });
              }}>
           </button>
        </div>
              <div className="weather">
        <Weather
          key={activePlace}
          name={PLACES[activePlace].name} />
          </div>
      </div>
    )
  }
};

export default App;
