import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Weather } from './Weather.js';
import './style.css'
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      active: '',
      name: 'Kemerovo',
      flag: false
    };
  }
  handleChange = (e) => {
    this.setState({ active: e.target.value });
  };
  onBtnClickHandler = (e) => {
      this.setState({name:this.state.active, flag:true})
    e.preventDefault();
  };
  afterBtn = (e) =>{
    this.setState({name:'', flag:false})
  }
  render() {
    return (
      <div className="container">
        <div className="row block1">
          <input active={this.state.active} onChange={this.handleChange} ></input>
          <button onClick={this.onBtnClickHandler}>
            Send
          </button>
        </div>

        <div className="weather">
          <Weather
            name={this.state.name}
          />

        </div>
      </div>

    ) 
  }
};

export default App;
