import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Weather } from './Weather.js';
import './style.css'
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      activePlace: false,
      name: 'Кемерово'

    };
  }
  handleChange = (e) => {
    this.setState({ name: e.target.value });
    console.log(this.state.activePlace)

  };
  onBtnClickHandler = (e) => {
    e.preventDefault();
    this.setState({ activePlace: true });

    console.log(this.state.name)
  };
  componentDidMount(){
    this.setState({ activePlace: false });
    console.log(this.state.activePlace)
  }
  render() {
    const activePlace = this.state.activePlace;
    return (
      <div className="container">
        <div className="row block1">
          <input name={this.state.name} onChange={this.handleChange} ></input>
          <button onClick={this.onBtnClickHandler}>
            Send
          </button>
        </div>

        <div className="weather">
        {activePlace &&  <Weather
            name={this.state.name}
          />}
        </div>
      </div>

    )
    this.state.activePlace = false;
  }
};

export default App;
