import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


class Weather extends React.Component {
    constructor() {
        super();
        this.state = {
            weatherData: null,
            flag: null
        };

    }

    componentDidMount() {
        const name = this.props.name;
        console.log(name);
        const URL = "http://api.openweathermap.org/data/2.5/weather?q=" +
            name +
            "&appid=b1b35bba8b434a28a0be2a3e1071ae5b&units=imperial";
        fetch(URL).then(res => res.json()).then(json => {
            this.setState({ weatherData: json });
        });
    }
    componentDidUpdate() {
        this.state.flag = this.props.active;
        if (this.state.flag) {
            const name = this.props.name;
            console.log(name);
            const URL = "http://api.openweathermap.org/data/2.5/weather?q=" +
                name +
                "&appid=b1b35bba8b434a28a0be2a3e1071ae5b&units=imperial";
            fetch(URL).then(res => res.json()).then(json => {
                this.setState({ weatherData: json });
            });
        }
        this.setState({flag:false}) ;
    }
    render() {
        const weatherData = this.state.weatherData;
        if (!weatherData) return <div>Loading</div>;
        const weather = weatherData.weather[0];
        const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";
        return (
            <div>
                <h1>
                    {weather.main} in {weatherData.name}
                    <img src={iconUrl} alt={weatherData.description} />
                </h1>
                <p>Current: {Math.trunc((weatherData.main.temp - 32) * (5 / 9))}°</p>
                <p>High: {Math.trunc((weatherData.main.temp_max - 32) * (5 / 9))}°</p>
                <p>Low: {Math.trunc((weatherData.main.temp_min - 32) * (5 / 9))}°</p>
                <p>Wind Speed: {(weatherData.wind.speed / 2.23).toFixed(1)} m/s</p>
            </div>
        );
    }
}
export { Weather };
